function Ic2c0=gen_Ic2c0(cRau,Rval,sval)%% Usage: Ic2c0=gen_Ic2c0(cRau,Rval,sval)%Rvalm3=Rval.^-3;% Rvalm2=Rval.^-2;% Rvalm1=Rval.^-1;%Ic2c0=zeros(length(sval),1);IRi_Rj=zeros(length(sval),1);svals=sval.^2;Fau=16.881901*exp(-0.461100/4*svals);Fau=Fau+18.591299*exp(-8.621600/4*svals);Fau=Fau+25.558201*exp(-1.482600/4*svals);Fau=Fau+5.860000*exp(-36.395599/4*svals);Fau=Fau+12.065800;Faus=Fau.^2;%qval=2*pi*sval;qvalm6=qval.^-6;%for i=1:length(Rval)	for j=i:length(Rval)		IRi_Rj=Faus.*qvalm6.*(sin(Rval(i)*qval)-Rval(i)*qval.*cos(Rval(i)*qval)).*(sin(Rval(j)*qval)-Rval(j)*qval.*cos(Rval(j)*qval));		sym=1+logical(i~=j);		Ic2c0=Ic2c0+sym*cRau(i)*Rvalm3(i)*cRau(j)*Rvalm3(j)*IRi_Rj;	 endend